// InputForm.js
import React from 'react';

const InputForm = ({
  city,
  country,
  state,
  onCountryChange,
  onStateChange,
  onSubmit
}) => {
  return (
    <div>
      <div>
        Enter a city
      </div>
      <input id="city" placeholder="ex London" value={city} onChange={onCityChange()} />

      <div>
        Optional: Enter a country
      </div>
      <input id="country" placeholder="ex United Kingdom" value={country} onChange={(e) => onCountryChange(e.target.value)} />

      <div>
        Optional: Enter a state 
      </div>
      <input id="state" placeholder="state name..." value={state} onChange={(e) => onStateChange(e.target.value)} />

      <button onClick={onSubmit}>Load Data</button>
    </div>
  );
}

export default InputForm;
