import React, { useState } from 'react';
import './App.css';
import InputForm from './InputForm';
import WeatherResult from './WeatherResult';

const App = () => {
  const [city, setCity] = useState('');
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const [result, setResult] = useState('');

  const loadWeather = async () => {
    const apikey = 'd5d0c58cec7185d2e06bf2f5b779e6be';
    try {

      if(city==''){
        setResult("Please enter in a city")
        return;
      }

      // We use this to fetch the latitude and longitude so we can then fetch the weather
      const responseCity = await fetch(`http://api.openweathermap.org/geo/1.0/direct?q=${city},${state},${country}&appid=${apikey}`);
      const data = await responseCity.json();

      if(data.length ==0){
        throw new Error("Invalid entry");
      }

      const { lat, lon, state: responseState, country: responseCountry } = data[0];
      const responseWeather = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apikey}&units=metric`);
      const weatherData = await responseWeather.json();


      if (data[0].country == "US") {
        if(country == ''){
          // For USA we include the state too as some states have cities with the same name
        setResult(`${weatherData.main.temp}°C: ${data[0].name.toUpperCase()}, ${responseState.toUpperCase()}, ${data[0].country.toUpperCase()}`);
        }
        else{
        setResult(`${weatherData.main.temp}°C: ${data[0].name.toUpperCase()}, ${responseState.toUpperCase()}, ${data[0].country.toUpperCase()}`);
        }
      } 

      else {
        if(country == ''){
        setResult(`${weatherData.main.temp}°C: ${data[0].name.toUpperCase()}, ${data[0].country.toUpperCase()}`);
      }
      else{
        setResult(`${weatherData.main.temp}°C: ${data[0].name.toUpperCase()}, ${country.toUpperCase()}`);
      }

      }
    } catch (error) {
      console.error("Error: " + error.message);
      setResult("Invalid entry");
    }
  };

  const onCityChange = (value) => {
    setCity(value);
  };

  const onCountryChange = (value) => {
    setCountry(value);
  };

  const onStateChange = (value) => {
    setState(value);
  };

  return (
    <div className="App">
      <InputForm
        city={city}
        country={country}
        state={state}
        onCityChange={onCityChange}
        onCountryChange={onCountryChange}
        onStateChange={onStateChange}
        onSubmit={loadWeather}
      />
      <WeatherResult result={result} />
    </div>
  );
}

export default App;
