// InputForm.js

import React from 'react';

const InputForm = ({
  city,
  country,
  state,
  onSubmit
}) => {
  return (
    <div>
      <div>
        Enter a city
      </div>
      <input id="city" placeholder="ex London" value={city} />

      <div>
        Optional: Enter a country
      </div>
      <input id="country" placeholder="ex United Kingdom" value={country}/>

      <div>
        Optional: Enter a state 
      </div>
      <input id="state" placeholder="state name..." value={state}/>

      <button onClick={onSubmit}>Load Data</button>
    </div>
  );
}

export default InputForm;
