import React, { useState } from 'react';
import './App.css';
import InputForm from './InputForm';
import WeatherResult from './WeatherResult';

const App = () => {
  const [city, setCity] = useState('');
  const [country, setCountry] = useState('');
  const [state, setState] = useState('');
  const [result, setResult] = useState('');


  const getCountryCode = async (country) => {
    try {
      const response = await fetch(`https://restcountries.com/v3.1/name/${country}`);
      if (!response.ok) {
        throw new Error("Country Name not found");
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("ERROR: " + error.message);
      setResult("Country name not found");
    }
  };

  const loadWeather = async () => {
    const apikey = 'd5d0c58cec7185d2e06bf2f5b779e6be';
    let countrycode = '';

    if (country !== '') {
      const countryData = await getCountryCode(country);
      countrycode = countryData[0].cca2;
    }

    try {
      const responseCity = await fetch(`http://api.openweathermap.org/geo/1.0/direct?q=${city},${state},${countrycode}&appid=${apikey}`);
      if (!responseCity.ok) {
        throw new Error(`HTTP error! status: ${responseCity.status}`);
      }
      console.log(`http://api.openweathermap.org/geo/1.0/direct?q=${city},${state},${countrycode}&appid=${apikey}`)
      const data = await responseCity.json();
      console.log(data)

      if (data[0].name.toLowerCase() !== city.toLowerCase()) {
        throw new Error("City name not found");
      }

      const { lat, lon, state: responseState, country: responseCountry } = data[0];
      const responseWeather = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apikey}&units=metric`);
      const weatherData = await responseWeather.json();

      if (responseState) {
        setResult(`${weatherData.main.temp}째C: ${city.toUpperCase()}, ${responseState.toUpperCase()}`);
      } else {
        setResult(`${weatherData.main.temp}째C: ${city.toUpperCase()}, ${country.toUpperCase()}`);
      }
    } catch (error) {
      console.error("Error: " + error.message);
      setResult("Incorrect entry");
    }
  };

  const onCityChange = () => {
    setCity();
  };

  const onCountryChange = (value) => {
    setCountry(value);
  };

  const onStateChange = (value) => {
    setState(value);
  };

  return (
    <div className="App">
      <InputForm
        city={city}
        country={country}
        state={state}
        onCityChange={onCityChange}
        onCountryChange={onCountryChange}
        onStateChange={onStateChange}
        onSubmit={loadWeather}
      />
      <WeatherResult result={result} />
    </div>
  );
}

export default App;
